const api_url = "https://api.spotify.com/v1";

/**
 * Spotify API Abstraction
 */
class Spotify {

	/**
	 * Fetches data
	 *
	 * @returns {Promise}
	 */
	static fetchData () {

		return new Promise((resolve, reject) => {

			if (!settings.spotify.refresh_token) {
				settings.spotify.error = true;
				return reject([null, true]);
			}

			refreshToken(error => {
				if (error) {
					settings.spotify.error = true;
					return reject([error, true]);
				}

				data.spotify = [];

				api.get('spotify', '/v1/me/playlists', settings.spotify.access_token, { limit: 50 }, (err, result) => {

					if (err) return reject([err]);

					for (let i of result.items) {

						!function outer(i) {

							api.get('spotify', i.tracks.href.split('.com')[1], settings.spotify.access_token, { limit: 100 }, (err, result) => {
								if (err) return reject([err]);

								let tempTracks = [];
								const isWeeklyDiscover = (i.tracks.href.indexOf("/spotifydiscover/") > -1);

								function moreTracks(url) {
									api.get('spotify', url.split('.com')[1], settings.spotify.access_token, { limit: 100 }, (err, result) => {
										if (err) return reject([err]);

										for (let t of result.items)
											tempTracks.push(convertTrack(t.track));

										if (result.next) moreTracks(result.next);

									});
								}

								if (result) {
									for (let t of result.items)
										tempTracks.push(convertTrack(t.track));

									if (result.next) moreTracks(result.next);
								}

								if (isWeeklyDiscover) {
									data.spotify.push({
										title: i.name,
										id: i.id,
										icon: 'compass',
										artwork: i.images[0].url,
										tracks: tempTracks
									});
								} else {
									data.spotify.push({
										title: i.name,
										id: i.id,
										artwork: (i.images[0] ? i.images[0].url : ''),
										tracks: tempTracks
									});
								}

							});

						}(i);
					}

					let tempMytracks = [];

					const addToSpotifyPlaylistFavs = (url) => {
						api.get('spotify', url, settings.spotify.access_token, { limit: 50 }, (err, result) => {
							if (err) return reject([err]);

							for (let i of result.items)
								tempMytracks.push(convertTrack(i.track));

							if (result.next) {
								addToSpotifyPlaylistFavs(result.next.split('.com')[1]);
							} else {

								data.spotify.unshift({
									title: 'My tracks',
									artwork: '',
									icon: 'spotify',
									id: 'favs',
									tracks: tempMytracks
								});

								resolve();
							}

						});
					};

					addToSpotifyPlaylistFavs('/v1/me/tracks');

				});


			})

		});


	}

	/**
	 * Called when user wants to activate the service
	 *
	 * @param callback {Function} Callback function
	 */
	static login (callback) {

		api.oauthLogin('spotify', (code) => {

			api.init('spotify', data.client_ids.spotify.client_id, data.client_ids.spotify.client_secret);

			api.auth('spotify', code, (error, data) => {

				if (error || data.error)
					return callback(`${error} + ${data.error}`);

				settings.spotify.refresh_token = data.refresh_token;
				callback();

			});

		});

	}

	/**
	 * Like a song 
	 *
	 * @param trackId {string} The track's id (Again, unused)
	 */
	static like (trackId) {
		api.put('spotify', '/v1/me/tracks?ids=' + g.playing.id, settings.spotify.access_token, {}, err => {
			if (err) {
				new Notification('Error liking track', {
					'body': err,
					'tag': 'Harmony-Error',
					'origin': 'Harmony'
				});
			}
		});
	}

	/**
	 * Unlike a song
	 *
	 * @param trackId {string} The track's id
	 */
	static unlike (trackId) {
		api.delete('spotify', '/v1/me/tracks?ids=' + g.playing.id, settings.spotify.access_token, {}, err => {
			if (err) {
				new Notification('Error liking track', {
					'body': err,
					'tag': 'Harmony-Error',
					'origin': 'Harmony'
				});
			}
		});
	}

	/**
	 * Get the streamable URL
	 *
	 * @param track {Object} The track object
	 * @param callback {Function} Callback function
	 */
	static getStreamUrl (track, callback) {
		api.getStreamUrlFromName(track.duration, `${track.artist.name} ${track.title}`, (err, streamUrl) => {
			if (err) {
				Player.nextTrack();
			} else {
				callback(streamUrl, track.id);
			}
		});
	}

	/**
	 * View the artist
	 *
	 * @param track {Object} The track object
	 */
	static viewArtist (track) {
		specialView('spotify', 'loading', 'artist', track.artist.name);

		request({ url: api_url+`/artists/${track.artist.id}`, method: "GET", json: true}, (err, result, body) => {
			if (err) return console.error(err);

			let image = body.images[0].url;

			request({ url: api_url+`/artists/${track.artist.id}/top-tracks?country=US`, method: "GET", json: true}, (err, result, body) => {
				if (err) return console.error(err);

				let tracks = [];

				for (let tr of body.tracks)
					if (tr) tracks.push(convertTrack(tr));


				specialView('spotify', tracks, 'artist', track.artist.name, image);
			});
		});
	}

	/**
	 * View an album
	 *
	 * @param track {Object} The track object
	 */
	static viewAlbum (track) {
		specialView('spotify', 'loading', 'album', track.album.name, track.artwork);

		request({ url:  api_url+`/albums/${track.album.id}/tracks`, method: "GET", json: true}, (err, result, body) => {
			if (err) return console.error(err);

			let tracks = [];

			for (let tr of body.items)
				if (tr) tracks.push(convertTrack(tr));

			specialView('spotify', tracks, 'album', track.album.name, track.artwork)

		});
	}

	/**
	* Search
	* @param query {String}: the query of the search
	* @param callback
	*/
	static searchTracks (query, callback) {

		request({ url:  api_url+'/search?type=track&q='+encodeURI(query), method: "GET", json: true}, (err, result, body) => {

			if (err) return console.error(err);
			let tracks = [];

			for (let tr of body.tracks.items)
				if (tr) tracks.push(convertTrack(tr));

			callback(tracks)

		});
	}

}

